<?phpinclude_once('inc/model.php');include_once('inc/view.php');// Информация для отображения.$title = 'Название сайта::Чтение';$text = text_get();// Внутренний шаблон.$content = view_include(	'theme/v_index.php',	array('text' => $text));$Date = date('Y');// Внешний шаблон.$page = view_include(	'theme/v_main.php', 	array('title' => $title, 'content' => $content, 'Date' => $Date, 'menu' => menu(), 'comments' => get_comment()));    // Вывод.echo $page;