tach();	$("#basketlink").append('<img class="basket" src="theme/images2/basket2.png">')});$("#basket").on("mouseleave", function(){	$("#basket").detach();	$("#basketlink").append('<img class="basket" src="theme/images2/basket1.png">')}); var heights = 500; var animationSpeed = 1000; var pause = 3000; var currentSlide = 1; var $slider = $('#slider'); var $slideContainer = $slider.find('.slides'); var $slides = $slideContainer.find('.slide');var interval;  function startSlider(){  interval = setInterval(function(){    $slideContainer.animate({'margin-top': '-='+heights}, animationSpeed, function(){	currentSlide++;	if(currentSlide === $slides.length){	 currentSlide = 1;	 $slideContainer.css('margin-top', 0);	 }	 });	},pause);   }   function stopSlider(){       clearInterval(interval);   }            $slider.on('mouseenter', stopSlider);$slider.on('mouseleave', startSlider);   startSlider();  var elem = document.getElementById("but1");var elem2 = document.getElementById("but2");var elem3 = document.getElementById("but3");var width = 1;var width2 = 1;var width3 = 1;var inter = setInterval(frame, 50);function frame(){   if(width >=60){      	  elem.style.width = 0.5;	  	  if(width2>=60){	    	    elem2.style.width = 0.5;				if(width3>=60){	    width = 1;		width3 = 1;			    elem3.style.width = 0.5;	    }	    else{	    width3++;	    elem3.style.width = width3 + 'px';	  }	  }	  else{	    width2++;	    elem2.style.width = width2 + 'px';			  }}      else{    width++;	elem.style.width = width + 'px';	width2 = 1;   }   }</script><style>@font-face{	font-family: BravoRG;	src: url(theme/BravoRG.otf);}ul{list-style-type: none;}#slider{    weidth: 0px;	height: 400px;	overflow: hidden;	}#slider.slides{    display: block;	width: 6000px;	height: 400px;	margin: 0;	padding: 0;	}#slider.slide{    float: left;		width: 720px;	height: 400px;}.button{    width: 60p